mapper.update:
UPDATE t_product
SET pic = #{pic}
WHERE product_id = #{productId}

UPDATE t_feature
SET
sweety = #{sweety},
acidity = #{acidity},
body = #{body}
WHERE feature_id = #{featureId}

mapper.select:
SELECT category_id categoryId, feature_id featureId, country_id countryId
FROM t_product
WHERE product_id = #{productId}

SELECT refund_id refundId, order_id orderId, refund_reason refundReason, refund_yn refundYn, refund_date refundDate
FROM t_order_refund
LIMIT #{startIdx}, #{row}

SELECT store_id storeId, region_nm_id regionNmId, nm, tel
FROM t_store
LIMIT #{startIdx}, #{row}

mapper.update:
UPDATE t_order
SET
order_status = #{orderStatus}
WHERE order_id = #{orderId}

UPDATE t_sale
SET
sale_yn = #{saleYn}

WHERE product_id = #{productId}
